import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ToastrService } from 'ngx-toastr';
import { BucketInfoModel, CheckProfileStatusResponseModel, ErrorDocModel, OptionsListModel } from 'src/app/_models/CheckProfileStatusResponseModel';
import { ProfileService } from 'src/app/_services/profile.service';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})

export class ProfileComponent implements OnInit {

  bucketInfo!: BucketInfoModel;
  optionsList!:OptionsListModel; 
  errorDoc!:ErrorDocModel; 
  //CheckProfileStatusResponseModel!: CheckProfileStatusResponseModel;
  formGroup!: FormGroup;
  DialField: FormControl = new FormControl;
  SourceIdField: FormControl = new FormControl;
  LangIdField: FormControl = new FormControl;
  bucket:any[] = [];  
  response: any;
  options: any[] = [];
  object!: string;




  constructor(private fb: FormBuilder,
    private toastr: ToastrService,
    public service: ProfileService) {
  }

  ngOnInit() {

    this.loadForm();

  }

  loadForm() {
    this.formGroup = this.fb.group({
      DialField: ["", [Validators.required]],
      SourceIdField: [""],
      LangIdField: [""],
      categoy:[""]
    });
  }

  Clear() {
      this.formGroup.reset();
       this.loadForm();
  }

  AddNewProfile() {
    if (this.formGroup.invalid) {
      this.HandleConditionalForm();
      return
    }
  console.log("AddNewProfile",this.formGroup.value  );
    return this.service.createNewProfile(this.formGroup.value)
      .subscribe((res) => {
       this.response=res;
       this.options=res.bucketInfo.optionsList
       this.bucket=res.bucketInfo;
       console.log(this.bucket);
       console.log(this.response);
     this.object= JSON.stringify(JSON.parse(res), null, 2);
        this.showToasterSuccess("Successfully Posted");
        this.Clear();

      },
        err => {
          console.log(err);
          this.showToasterError(err.error);
        });
  }
  HandleConditionalForm(): any {
    if (this.formGroup.get("dialField")?.invalid)
      return this.showToasterError('Invalid Dial Number');
  }
  showToasterError(message: string) {
   return this.toastr.error(message)
  }

  public showToasterSuccess(message: string) {
   return this.toastr.success(message)
  }

}
